import React, { useEffect, useState } from 'react';
import { PublicKey, Transaction, SystemProgram, Connection } from '@solana/web3.js';
import { useWallet } from '@solana/wallet-adapter-react';

// Corrected paths to images, assuming they are in the public/assets/images folder
const logo = '/assets/images/logo.png';  // Logo image
const facebookIcon = '/assets/images/facebook.png';  // Example social icon
const twitterIcon = '/assets/images/twitter.png';  // Example social icon

const Wallet = () => {
  const { publicKey, sendTransaction } = useWallet();  // Wallet provider hook
  const [recipientPublicKey, setRecipientPublicKey] = useState('');
  const [amount, setAmount] = useState('');

  useEffect(() => {
    setRecipientPublicKey('RECIPIENT_PUBLIC_KEY'); // Replace with your recipient public key
  }, []);

  const handleSendTransaction = async () => {
    if (!publicKey || !recipientPublicKey || !amount) {
      alert('Please fill in all fields!');
      return;
    }

    const senderPublicKey = publicKey;
    const recipient = new PublicKey(recipientPublicKey);
    const connection = new Connection('https://api.mainnet-beta.solana.com', 'confirmed');

    const transaction = new Transaction().add(
      SystemProgram.transfer({
        fromPubkey: senderPublicKey,
        toPubkey: recipient,
        lamports: parseFloat(amount) * 1000000000, // Convert SOL to lamports
      })
    );

    try {
      const signature = await sendTransaction(transaction, connection);
      await connection.confirmTransaction(signature, 'processed');
      alert('Transaction successful!');
    } catch (error) {
      console.error('Transaction failed:', error);
      alert('Transaction failed. Please try again.');
    }
  };

  return (
    <div style={{ textAlign: 'center' }}>
      <header>
        <img src={logo} alt="Logo" style={{ width: '200px', marginBottom: '20px' }} />
        <h2>Solana Wallet</h2>
      </header>

      <div>
        <h3>Send Solana Transaction</h3>
        <div>
          <label>
            Recipient Public Key:
            <input
              type="text"
              value={recipientPublicKey}
              onChange={(e) => setRecipientPublicKey(e.target.value)}
              placeholder="Recipient Public Key"
            />
          </label>
        </div>
        <div>
          <label>
            Amount (in SOL):
            <input
              type="number"
              value={amount}
              onChange={(e) => setAmount(e.target.value)}
              placeholder="Amount"
            />
          </label>
        </div>
        <button onClick={handleSendTransaction}>Send Transaction</button>
      </div>

      <footer>
        <div>
          <a href="https://facebook.com" target="_blank" rel="noopener noreferrer">
            <img src={facebookIcon} alt="Facebook" style={{ width: '40px', margin: '0 10px' }} />
          </a>
          <a href="https://twitter.com" target="_blank" rel="noopener noreferrer">
            <img src={twitterIcon} alt="Twitter" style={{ width: '40px', margin: '0 10px' }} />
          </a>
        </div>
        <p>&copy; 2024 Solana Wallet</p>
      </footer>
    </div>
  );
};

export default Wallet;
